    public static int water(int[] height){
        int max = 0;
        int left = 0, right = height.length-1;
        int count = 0;

        while(left != right){
            if(height[left] > height[right]){
                max = Math.max(max, height[right] * (right-left));
                right--;
            }else{
                max = Math.max(max, height[left] * (right-left));
                left++;
            }
        }

        return max;
    }
